sourcesContent:
import {getConnection} from './../database/database';
import jwt from "jsonwebtoken";

const index = async(request, response) => {
    try {
        var userID = 0;
        jwt.verify(request.token, 'secretKey', (error, dataUser) =>{
            if(error){
                response.json(error.message)
            }else{
                userID = dataUser.user.id;
            }
        })
        var token = request.token;
        // response.json(token)

        if(token === undefined){
            response.json({message: "A ocurrido un pequeo problema"})
        }

        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM materias");
        // response.send("DIMENCION"+result.length)
        if(result.length>0){
            response.json({
                data: result,
                message: 'success'
            })
        }else{
            response.json({message: 'No podemos obtener informacin'})
        }
    } catch (error) {
        response.json({message: "A ocurrido un problema con tu peticin, parece que el servidor no responde intntalo mas tarde ("+error.message+")"})
    }
}

const getOne = async(request, response) =>{
    try {
        var userID = 0;
        jwt.verify(request.token, 'secretKey', (error, dataUser) =>{
            if(error){
                response.json(error.message)
            }else{
                userID = dataUser.user.id;
            }
        })
        var token = request.token;
        var {id} = request.params
        // response.json(token)

        if(token === undefined){
            response.json({message: "A ocurrido un pequeo problema"})
        }

        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM materias WHERE id_materia = ?", id);
        // response.send("DIMENCION"+result.length)
        if(result.length>0){
            response.json({
                data: result,
                message: 'success'
            })
        }else{
            response.json({message: 'No podemos obtener informacin'})
        }
    } catch (error) {
        response.json({message: "A ocurrido un problema con tu peticin, parece que el servidor no responde intntalo mas tarde ("+error.message+")"})
    }
}

const registrar = async(request, response)=>{
    try {
        var userID = 0;
        jwt.verify(request.token, 'secretKey', (error, dataUser) =>{
            if(error){
                response.json(error.message)
            }else{
                userID = dataUser.user.id;
            }
        })
        var token = request.token;

        if(token === undefined){
            response.json({message: "A ocurrido un pequeo problema"})
        }

        var {nombre_materia, cantidad_horas_materia} = request.body;
        if((nombre_materia === undefined) || (cantidad_horas_materia === undefined)){
            response.json({message: "Llena todos los campos"})
        }
        const materias = {
            nombre_materia,
            cantidad_horas_materia
        };

        const connection = await getConnection()
        await connection.query("INSERT INTO materias SET ?", materias);
        response.json({ message: "success" });

    } catch (error) {
        response.json({message: "A ocurrido un problema con tu peticin, parece que el servidor no responde intntalo mas tarde ("+error.message+")"})
    }
}

const actualizar = async (request, response)=>{
    try {
        var userID = 0;
        jwt.verify(request.token, 'secretKey', (error, dataUser) =>{
            if(error){
                response.json(error.message)
            }else{
                userID = dataUser.user.id;
            }
        })
        var token = request.token;

        if(token === undefined){
            response.json({message: "A ocurrido un pequeo problema"})
        }

        var {nombre_materia, cantidad_horas_materia} = request.body;
        var {id} = request.params
        if((nombre_materia === undefined) || (cantidad_horas_materia === undefined)){
            response.json({message: "Llena todos los campos"})
        }
        const materia = {
            nombre_materia,
            cantidad_horas_materia
        };

        const connection = await getConnection()
        const result = await connection.query("UPDATE materias SET ? WHERE id_materia = ?", [materia, id])
        if(result.affectedRows !== 0){
			response.json({message: "success"})
		}else{
			response.json({message: "No usar contraseas anteriores"})
		}

    } catch (error) {
        response.json({message: "A ocurrido un problema con tu peticin, parece que el servidor no responde intntalo mas tarde ("+error.message+")"})
    }
}

const eliminar = async (request, response)=>{
    try {
        var userID = 0;
        jwt.verify(request.token, 'secretKey', (error, dataUser) =>{
            if(error){
                response.json(error.message)
            }else{
                userID = dataUser.user.id;
            }
        })
        var token = request.token;

        if(token === undefined){
            response.json({message: "A ocurrido un pequeo problema"})
        }

        var {id} = request.params

        const connection = await getConnection()
        const result = await connection.query("DELETE FROM materias WHERE id_materia = ?", id)
        if(result.affectedRows !== 0){
			response.json({message: "success"})
		}else{
			response.json({message: "No usar contraseas anteriores"})
		}

    } catch (error) {
        response.json({message: "A ocurrido un problema con tu peticin, parece que el servidor no responde intntalo mas tarde ("+error.message+")"})
    }
}

export const methods = {
    index,
    registrar,
    actualizar,
    eliminar,
    getOne
};

