sourcesContent:
import {getConnection} from "./../database/database";
import jwt from "jsonwebtoken";

const encontrarEstudiante = async (request, response)=>{
    try {
        const identificacion_estudiante = request.body.identificacion_estudiante;
        const connection = await getConnection();
        const result = await connection.query('SELECT * FROM estudiantes WHERE identificacion_estudiante=?', identificacion_estudiante);

        if(typeof result[0] !== 'undefined'){
            var $data = {
                nombre: result[0].nombre_estudiante,
                apellido: result[0].apellido_estudiante,
                email: result[0].correo_estudiante,
                message:"success"
            };
            response.json($data);
        }else{
            response.json({message: "Usuario no encontrado"})
        }
        
    } catch (error) {
        response.json({message: "A ocurrido un problema con tu peticin, parece que el servidor no responde intntalo mas tarde ("+error.message+")"})
    }
}

const index = async(request, response) => {
    try {
        var userID = 0;
        jwt.verify(request.token, 'secretKey', (error, dataUser) =>{
            if(error){
                response.json(error.message)
            }else{
                userID = dataUser.user.id;
            }
        })
        var token = request.token;
        // response.json(token)

        if(token === undefined){
            response.json({message: "A ocurrido un pequeo problema"})
        }

        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM estudiantes");
        // response.send("DIMENCION"+result.length)
        if(result.length>0){
            response.json({
                data: result,
                message: 'success'
            })
        }else{
            response.json({message: 'No podemos obtener informacin'})
        }
    } catch (error) {
        response.json({message: "A ocurrido un problema con tu peticin, parece que el servidor no responde intntalo mas tarde ("+error.message+")"})
    }
}

const getOne = async(request, response) =>{
    try {
        var userID = 0;
        jwt.verify(request.token, 'secretKey', (error, dataUser) =>{
